import './App.css';

import Boton from './componentes/Boton';
import Arma from './componentes/Arma';
import Pantalla from './componentes/Pantalla';
import BotonClear from './componentes/BotonClear';
import Logo from './componentes/Logo';

//Hook
import {useState} from 'react';
import {evaluate, prodDependencies} from 'mathjs';

function App() {

  const [input, setInput] = useState('');
  const [input2, setInput2] = useState('');


  const agregarInput = val => {
    setInput(input + val);
  };


  const agregarInput2 = val => {
    setInput2(input2 + "val");
  };
  //const agregarInput = val => {
    //setInput(input + val);
  //};

  //const calcularResultado = () => {
     //if(input){
      // setInput(evaluate(input));
    // }else{
     //  alert("Por favor ingrese valores para realizar los calculos");
    // }
  // };

  function getRandomInt(max) {
    return Math.floor(Math.random() * max);
  }
  
  const calcularResultado = () => {
     if(getRandomInt(100) <= 50){
      setInput(input + "val");
     }else{
       alert("Por favor ingrese valores para realizar los calculos");
     }
   };

   const arma = () => {
    
    setInput2("Arma");
    
  };

  

  return (
    <div className="App">


        <div className='contenedor-calculadora'>
        <div className='text'>
            
            Item Upgrade

          </div>

          <div className='arma'>
            
          <Arma manejarClic={arma}>Demon Slayer</Arma>

          </div>

          <NombreArma input2={input2}/>
          <div className='itemnombre'>
            

          </div>
          <div className='chances'>
            
            Chance to upgrade: 70%

          </div>
          <div className='fila'>
            <Boton manejarClic={calcularResultado}>Upgrade</Boton>
            
            <BotonClear manejarClear={() => setInput('')}>Cancel</BotonClear>
          </div>



        </div>

    </div>
  );

}

export default App;
